DECLARE

@CR_SQL NVARCHAR(2000),
@ACCOUNT_CODE_CR INT
BEGIN TRAN
BEGIN




 SET @CR_SQL=N''DELETE FROM ''+@TO_DATA+''.[dbo].[T_BOX] WHERE  BILL_NUMBER=0 AND B_CODE=13''
 exec (@CR_SQL)

--OPEN BOX_CURSOR

