CREATE PROCEDURE [dbo].[P_API_DELETE_INVOICE]

@BILL_NUMBER INT ,
@USER_CREATE NVARCHAR(MAX)
AS
DECLARE
@FLAG INT=0
 

 BEGIN
BEGIN TRANSACTION;
UPDATE T_SALES SET OFFER_CODE=0, ACTIVE =@FLAG,USER_UPDATE=N''   : ''+ @USER_CREATE+N'' - ''+ CONVERT(VARCHAR(20),GETDATE(),100) WHERE BILL_NUMBER = @BILL_NUMBER 
