           ,[CASH_IQ]
           ,[REMAIN_IQ]
           ,[C_DATE]
           ,[C_UPDATE]
           ,[USER_UPDATE]
           ,[ACTIVE]
           ,[IS_AVG_COST]
           ,[AVG_COST]
           ,[FLAG_CASH]
           ,[IS_OUT]
           ,[CURRANCY_SALES]
           ,[IS_TRANS]
           ,[CENTER_CODE]
           ,[QR_DATA]
           ,[ITEM_ID_PAYMENT]
           ,[TAG_PROCCESS_CODE]
           ,[LINK_ID_PAYMENT]
           ,[BILL_RETURN]
           ,[BILL_SANAD_CNY]
           ,[CNY_IQ]
           ,[IS_PREPARE]
           ,[DRIVER_CODE]
           ,[LAT]
           ,[LNG]
           ,[STATUS_SHIPING]
           ,[MOVE_CODE]
           ,[BILL_ORDER]
           ,[TOTAL_TAX_ITEM]
           ,[TOTAL_MANDOOB_ITEM]
           ,[TOTAL_ADD_COST]
           ,[TOTAL_ADD_AMO]
           ,[SHIP_CODE]
           ,[COST_SHIP]
           ,[IS_POINT]
           ,[COST_SHIP_CLIENT]
           ,[USER_TAG_CODE]
           ,[RELATION_BILL_CODE]
           ,[RELATION_B_CODE]
           ,[REGION_CODE]
