           ,GETDATE() as [C_DATE]
           ,N'''' as [C_UPDATE]
           ,N'''' as [USER_UPDATE]
           ,1 as [ACTIVE]
           ,0 as [IS_AVG_COST]
           ,0 as [AVG_COST]
           ,IIF( max(case when name=''REMAIN_AMO'' then convert(float,StringValue) else 0 end)=0,1,0) as [FLAG_CASH]
           ,1 as [IS_OUT]
           ,max(case when name=''CURRANCY_EQUAL'' then convert(int,StringValue) else 1 end) as [CURRANCY_SALES]
           ,0 as [IS_TRANS]
           ,0 as [CENTER_CODE]
