	BEGIN
	SELECT 0 AS ID, N''    ..  '' AS  N''Response'' FROM T_CLIENT WHERE CLIENT_CODE=2100000
	ROLLBACK 
 
SELECT 0 AS ID, N''    ..  '' AS  N''Response'' FROM T_CLIENT WHERE CLIENT_CODE=2100000
	RETURN
	END

return
 

