@BALANCEEE_FIRST		float	,
@BALANCEEE_FIRST_IQ		float	,
@BALANCEEE_CUR		float	,
@BALANCEEE_FIRST_CUR		float	,
@MANDOB_CODE	int	,
@MANDOB_NAME  nvarchar(max), 
@CLIENT_REGION  nvarchar(max), 
@CLIENT_PHONE_SMS nvarchar(max), 
@DEAL_CODE	int	,
@DEAL_NAME	nvarchar(MAX)	,
@B_COLOR	nvarchar(MAX),

