 SELECT @BALANCEEE_IQ =ISNULL(SUM(AMO_DEBIT_IQ -AMO_CREDIT_IQ ),0) FROM T_BOX_REPORT_ACCOUNT WHERE   T_ID_BOX <= @ID_REPORT 
 SELECT @BALANCEEE_CUR =ISNULL(SUM(AMO_DEBIT_CUR -AMO_CREDIT_CUR ),0) FROM T_BOX_REPORT_ACCOUNT WHERE   T_ID_BOX <= @ID_REPORT 
 
 SET @REMAIN_USA=(@DE_USA-@CR_USA)+@BALANCEEE_FIRST
 SET @REMAIN_IQ=(@DE_IQ-@CR_IQ)+@BALANCEEE_FIRST_IQ
 SET @REMAIN_CUR=(@DE_CUR-@CR_CUR)+@BALANCEEE_FIRST_CUR
INSERT INTO [dbo].[T_BOX_REPORT_ACCOUNT]
           ([T_ID_BOX]
           ,[BILL_NUMBER]
           ,[BILL_DATE]
           ,[BILL_TIME]
           ,[B_CODE]
           ,[B_NAME]
           ,[MOVE_CODE]
           ,[MOVE_NAME]
           ,[ACCOUNT_CODE]
           ,[ACCOUNT_NAME]
           ,[ACCOUNT_DESCRIPTION]
           ,[AMO_DEBIT]
           ,[AMO_CREDIT]
           ,[BALANCE_USA]
           ,[AMO_DEBIT_IQ]
           ,[AMO_CREDIT_IQ]
           ,[BALANCE_IQ]
           ,[AMO_DEBIT_CUR]
           ,[AMO_CREDIT_CUR]
           ,[BALANCE_CUR]
           ,[DESCRIPTION]
           ,[USER_CODE]
           ,[USER_NAME]
           ,[DEAL_CODE]
           ,[DEAL_NAME]
           ,[BRANCH_CODE]
           ,[BRANCH_NAME]
           ,[MANDOB_NAME]
           ,[CLIENT_REGION]
           ,[CLIENT_PHONE_SMS]
		   ,[B_COLOR]
		   ,[CHECKED]
		   ,[ID_BOX])
     VALUES
           (@ID_REPORT
           ,@BILL_NUMBER
           ,@BILL_DATE
           ,@BILL_TIME
           ,@B_CODE
           ,@B_NAME
           ,@MOVE_CODE
           ,@MOVE_NAME
           ,@ACCOUNT_CODE
           ,@ACCOUNT_NAME
           ,@ACCOUNT_DESCRIPTION
           ,@DE_USA
           ,@CR_USA
           ,@BALANCEEE +@REMAIN_USA
           ,@DE_IQ
           ,@CR_IQ
           ,@BALANCEEE_IQ +@REMAIN_IQ
			,@DE_CUR
           ,@CR_CUR
           ,@BALANCEEE_CUR +@REMAIN_CUR
           ,@DESCRIPTION
           ,@USER_CODE
           ,@USER_NAME
           ,@DEAL_CODE
           ,@DEAL_NAME
           ,0
           ,NULL
           ,@MANDOB_NAME
           ,@CLIENT_REGION
           ,@CLIENT_PHONE_SMS
		   ,@B_COLOR
		   ,@CHECKED
		   ,@T_ID_BOX_CURCOR);

IF @B_CODE=2
BEGIN

 INSERT INTO [dbo].[T_BOX_REPORT_ACCOUNT_DETAILS]
