           ,[ITEM_NAME]
           ,[QTY]
           ,[PRICE]
           ,[TOTAL]
		   ,[MANDOOB_NAME]
           ,[WIDTH_CM]
           ,[LENGTH_CM]
           ,[AREA_M2]
           ,[DRIVER_NAME]
           ,[NOTE_PRINT])
  SELECT 
           @ID_REPORT
           ,ITEM_NAME
           ,QTY
           ,PRICE
           ,NET_TOTAL
		   ,[MANDOOB_NAME]
           ,[WIDTH_CM]
           ,[LENGTH_CM]
           ,[AREA_M2]
           ,[DRIVER_NAME]
           ,[NOTE_PRINT] FROM V_SALES_INVOICE_DETAILS WHERE BILL_NUMBER=@BILL_NUMBER AND ACTIVE=1
END
ELSE IF @B_CODE=21
BEGIN
INSERT INTO [dbo].[T_BOX_REPORT_ACCOUNT_DETAILS]
           ([ID_REPORT]
           ,[ITEM_NAME]
           ,[QTY]
           ,[PRICE]
