ELSE IF @B_CODE=22
BEGIN
INSERT INTO [dbo].[T_BOX_REPORT_ACCOUNT_DETAILS]
           ([ID_REPORT]
           ,[ITEM_NAME]
           ,[QTY]
           ,[PRICE]
           ,[TOTAL] )
  SELECT 
           @ID_REPORT
           ,ITEM_NAME
           ,QTY
           ,PRICE
           ,TOTAL FROM V_BUY_DETAILS_RETURN WHERE BILL_NUMBER=@BILL_NUMBER
END



FETCH NEXT FROM BOX_CURSOR INTO @T_ID_BOX_CURCOR

END

CLOSE BOX_CURSOR
DEALLOCATE BOX_CURSOR

IF @CNY_CODE=1
