CREATE PROCEDURE [dbo].[P_API_SPEEDOO_PHONE_GET_ITEM_MOVE_AVG]


@ITEM_CODE INT 

AS
DECLARE 
@BILL_SEQ decimal(18, 0)

 
IF OBJECT_ID(''tempdb..#TEMP_AVG_ITEM'') IS NOT NULL DROP Table #TEMP_AVG_ITEM
SELECT CAST( ROW_NUMBER() OVER (ORDER BY BILL_DATE ASC,BILL_NUMBER ASC) AS FLOAT) AS RowNum, x.* INTO #TEMP_AVG_ITEM FROM 
( SELECT 0 AS SEQ,ITEM_CODE, ITEM_NAME,N'''' AS ITEM_SIGN,CATEGORY_CODE,CATEGORY_NAME,STORE_CODE,STORE_NAME,QTY,UNIT_NAME,UNIT_QTY,(QTY*UNIT_QTY)AS QTY_MIN,(QTY*UNIT_QTY) AS QTY_MIN_BALANCE ,COST AS PRICE,TOTAL_COST AS TOTAL,''2000-01-01'' AS BILL_DATE,BILL_NUMBER,BILL_BRANCH,0 AS B_CODE,N'' '' AS B_NAME,(N''  '') AS DESCRIPTION_TYPE,T_ID AS GUID_ID,CAST(0 AS FLOAT) AS AVG_COST,CAST(0 AS FLOAT) AS TOTAL_AVG,CAST(0 AS FLOAT) AS BALANCE_MIN_AVG ,CAST(0 AS FLOAT) AS TOTAL_BALANCE,CAST(0 AS FLOAT) AS PROCCESS_ID  FROM V_BUY_DETAILS_FIRST WHERE ITEM_CODE=@ITEM_CODE
UNION 
SELECT 1 AS SEQ,ITEM_CODE, ITEM_NAME,ITEM_SIGN,CATEGORY_CODE,CATEGORY_NAME,STORE_CODE,STORE_NAME,QTY,UNIT_NAME,UNIT_QTY,(QTY*UNIT_QTY)AS QTY_MIN,(QTY*UNIT_QTY) AS QTY_MIN_BALANCE ,COST/UNIT_QTY AS PRICE,TOTAL_COST AS TOTAL,BILL_DATE,BILL_NUMBER,BILL_BRANCH,21 AS B_CODE,N'''' AS B_NAME,(N'' :''+CLIENT_NAME) AS DESCRIPTION_TYPE,T_ID AS GUID_ID,CAST(0 AS FLOAT) AS AVG_COST,CAST(0 AS FLOAT) AS TOTAL_AVG,CAST(0 AS FLOAT) AS BALANCE_MIN_AVG,CAST(0 AS FLOAT) AS TOTAL_BALANCE,CAST(0 AS int) AS PROCCESS_ID FROM V_BUY_DETAILS WHERE ITEM_CODE=@ITEM_CODE AND QTY<>0
UNION 
SELECT 2 AS SEQ,ITEM_CODE, ITEM_NAME,ITEM_SIGN,CATEGORY_CODE,CATEGORY_NAME,STORE_CODE,STORE_NAME,TOTAL_QTY AS QTY,UNIT_NAME,UNIT_QTY,(TOTAL_QTY*UNIT_QTY)AS QTY_MIN,(TOTAL_QTY*UNIT_QTY)*-1 AS QTY_MIN_BALANCE ,PRICE,TOTAL,BILL_DATE,BILL_NUMBER,BILL_BRANCH,2 AS B_CODE ,N'''' AS B_NAME ,(N'' :''+CLIENT_NAME) AS DESCRIPTION_TYPE,T_ID AS GUID_ID,CAST(0 AS FLOAT) AS AVG_COST,CAST(0 AS FLOAT) AS TOTAL_AVG,CAST(0 AS FLOAT) AS BALANCE_MIN_AVG,CAST(0 AS FLOAT) AS TOTAL_BALANCE,CAST(0 AS FLOAT) AS PROCCESS_ID FROM V_SALES_DETAILS WHERE ITEM_CODE=@ITEM_CODE 
 UNION 
SELECT 3 AS SEQ,ITEM_CODE, ITEM_NAME,N''''AS ITEM_SIGN,CATEGORY_CODE,CATEGORY_NAME,STORE_CODE,STORE_NAME,QTY,UNIT_NAME,UNIT_QTY,(QTY*UNIT_QTY)AS QTY_MIN,(QTY*UNIT_QTY) AS QTY_MIN_BALANCE ,COST/UNIT_QTY AS PRICE,TOTAL_COST AS TOTAL,  BILL_DATE,BILL_NUMBER,BILL_BRANCH,24 AS B_CODE,N''   '' AS B_NAME,IIF(QTY<0,N'''' ,N'' '') AS DESCRIPTION_TYPE,T_ID AS GUID_ID,CAST(0 AS FLOAT) AS AVG_COST,CAST(0 AS FLOAT) AS TOTAL_AVG,CAST(0 AS FLOAT) AS BALANCE_MIN_AVG ,CAST(0 AS FLOAT) AS TOTAL_BALANCE,CAST(0 AS FLOAT) AS PROCCESS_ID FROM V_BUY_DETAILS_CORRECT WHERE ITEM_CODE=@ITEM_CODE
UNION 
