        VALUES (@USER_CODE, N''   '', @MAX_SELL, 0, N'''');

        --   "   "
        INSERT INTO [dbo].[T_UNIT_FORM_INFO] ([USER_CODE], [L_NAME], [INFO_NAME], [VALUE_ID], [VALUE_NAME])
        VALUES (@USER_CODE, N''   '', @MIN_SELL, 0, N'''');

        --   ""  T_ITEM_UNIT  (NOLOCK)
        SELECT TOP (1)
            @UNIT_NAME = UNIT_NAME
        FROM T_ITEM_UNIT WITH (NOLOCK)
        WHERE ITEM_CODE = @ITEM_CODE AND UNIT_CODE = 1;

        --   ""
        INSERT INTO [dbo].[T_UNIT_FORM_INFO] ([USER_CODE], [L_NAME], [INFO_NAME], [VALUE_ID], [VALUE_NAME])
        VALUES (@USER_CODE, N'''', @UUNIT_NAME, @UNIT_QTY, @UNIT_NAME);

        --     
        SET @SUBMIT_FLAG = 1;
    END TRY
    BEGIN CATCH
        --           
        SET @SUBMIT_FLAG = 0;
        THROW;
    END CATCH
END;
' 
END
