        QTY,
        PRICE,
        TOTAL
    )
    VALUES
    (
        0,
        NULL,
        NULL,
        NULL,
        NULL
    );

    ------------------------------------------------------------------------
    -- 5)   T_BOX  +   .
    --       (Window Function)   .
    ------------------------------------------------------------------------
    ;WITH CTE_BOX AS
    (
        SELECT
            ROW_NUMBER() OVER (
                ORDER BY T.BILL_DATE ASC, T.T_ID_BOX ASC
            ) AS RowNum,   --  /  

            T.T_ID_BOX       AS BOX_KEY,
