        MOVE_NAME,
        ACCOUNT_CODE,
        ACCOUNT_NAME,
        ACCOUNT_DESCRIPTION,
        DE_USA,
        CR_USA,
        RUN_BALANCE_USA,
        DE_IQ,
        CR_IQ,
        RUN_BALANCE_IQ,
        DE_CUR,
        CR_CUR,
        RUN_BALANCE_CUR,
        [DESCRIPTION],
        USER_CODE,
        USER_NAME,
        DEAL_CODE,
        DEAL_NAME,
        0,                   -- BRANCH_CODE 
        NULL,                -- BRANCH_NAME
        @MANDOB_NAME,
        @CLIENT_REGION,
        @CLIENT_PHONE_SMS,
        B_COLOR,
        [CHECKED],
        BOX_KEY  --     T_BOX   ID_BOX   
    FROM CTE_BOX
    ORDER BY 
        RowNum;   --  RowNum = BILL_DATE ASC, T_ID_BOX ASC

    ------------------------------------------------------------------------
    -- 7)      (B_CODE)
    --          T_BOX_REPORT_ACCOUNT
    ------------------------------------------------------------------------
    ;WITH CTE_REPORT AS
    (
        SELECT T_ID_BOX AS ID_REPORT,  --     
               BILL_NUMBER,
               B_CODE
        FROM T_BOX_REPORT_ACCOUNT
        WHERE T_ID_BOX <> 0  --    
    )
    INSERT INTO T_BOX_REPORT_ACCOUNT_DETAILS
    (
        ID_REPORT,
        ITEM_NAME,
        QTY,
        PRICE,
        TOTAL,
        MANDOOB_NAME,
        WIDTH_CM,
        LENGTH_CM,
        AREA_M2,
        DRIVER_NAME,
        NOTE_PRINT,
        COL_STR1,
        COL_STR2,
        COL_STR3,
        COL_INT1,
        COL_INT2,
        COL_INT3,
        NOTE_PRINT_2,
        ITEM_NOTE_SALES
    )
    ----------------------------------------------------------------------------
    -- (1) & (2) =>   (V_SALES_INVOICE_DETAILS)
    ----------------------------------------------------------------------------
    SELECT 
        R.ID_REPORT,
        S.ITEM_NAME,
        S.QTY,
        S.PRICE,
        S.NET_TOTAL,
        S.MANDOOB_NAME,
        S.WIDTH_CM,
        S.LENGTH_CM,
        S.AREA_M2,
        S.DRIVER_NAME,
        S.NOTE_PRINT,
        S.COL_STR1,
        S.COL_STR2,
        S.COL_STR3,
        S.COL_INT1,
        S.COL_INT2,
        S.COL_INT3,
        S.NOTE_PRINT_2,
        S.ITEM_NOTE_SALES
    FROM CTE_REPORT R
    JOIN V_SALES_INVOICE_DETAILS S ON S.BILL_NUMBER = R.BILL_NUMBER
                                  AND S.ACTIVE     = 1
    WHERE R.B_CODE IN (1, 2)
    
    UNION ALL
 

    ----------------------------------------------------------------------------
    -- (21) =>   (V_BUY_DETAILS)
    ----------------------------------------------------------------------------
    SELECT
        R.ID_REPORT,
        B.ITEM_NAME,
        B.QTY,
        B.PRICE,
        B.TOTAL,
        NULL,  -- MANDOOB_NAME
        NULL,  -- WIDTH_CM
        NULL,  -- LENGTH_CM
        NULL,  -- AREA_M2
        NULL,  -- DRIVER_NAME
        NULL,   -- NOTE_PRINT
		NULL,   -- S.COL_STR1,
        NULL,   -- S.COL_STR2,
        NULL,   -- S.COL_STR3,
        NULL,   -- S.COL_INT1,
        NULL,   -- S.COL_INT2,
        NULL,   -- S.COL_INT3,
        NULL,   -- S.NOTE_PRINT_2,
        NULL    --  S.ITEM_NOTE_SALES
    FROM CTE_REPORT R
    JOIN V_BUY_DETAILS B ON B.BILL_NUMBER = R.BILL_NUMBER
    WHERE R.B_CODE = 21

    UNION ALL
    ----------------------------------------------------------------------------
    -- (22) =>    (V_BUY_DETAILS_RETURN)
    ----------------------------------------------------------------------------
    SELECT
        R.ID_REPORT,
        BR.ITEM_NAME,
        BR.QTY,
        BR.PRICE,
        BR.TOTAL,
        NULL,  -- MANDOOB_NAME
        NULL,  -- WIDTH_CM
        NULL,  -- LENGTH_CM
        NULL,  -- AREA_M2
        NULL,  -- DRIVER_NAME
         NULL,   -- NOTE_PRINT
		NULL,   -- S.COL_STR1,
        NULL,   -- S.COL_STR2,
        NULL,   -- S.COL_STR3,
        NULL,   -- S.COL_INT1,
        NULL,   -- S.COL_INT2,
        NULL,   -- S.COL_INT3,
        NULL,   -- S.NOTE_PRINT_2,
        NULL    --  S.ITEM_NOTE_SALES
    FROM CTE_REPORT R
    JOIN V_BUY_DETAILS_RETURN BR ON BR.BILL_NUMBER = R.BILL_NUMBER
    WHERE R.B_CODE = 22;

    ------------------------------------------------------------------------
    -- 8)        @CNY_CODE
    ------------------------------------------------------------------------
    IF @CNY_CODE = 1
    BEGIN
        DELETE FROM T_BOX_REPORT_ACCOUNT
        WHERE T_ID_BOX <> 0
          AND AMO_DEBIT = 0
          AND AMO_CREDIT = 0;
    END
    ELSE IF @CNY_CODE = 2
    BEGIN
        DELETE FROM T_BOX_REPORT_ACCOUNT
        WHERE T_ID_BOX <> 0
          AND AMO_DEBIT_IQ = 0
          AND AMO_CREDIT_IQ = 0;
    END;

    ------------------------------------------------------------------------
    -- 9)  
    ------------------------------------------------------------------------
    SET @SUBMIT_FLAG = 1;
END;
' 
END
