SELECT @BALANCEEE_FIRST_IQ =ISNULL(SUM(DEBIT_IQ -CREDIT_IQ ),0) FROM T_BOX WHERE ACCOUNT_CODE=@ACCOUNT_CODE AND T_ID_BOX <= @IDD and ACTIVE=1
