           ,@BALANCEEE_CUR +@REMAIN_CUR
           ,@DESCRIPTION
           ,@USER_CODE
           ,@USER_NAME
           ,@DEAL_CODE
           ,@DEAL_NAME
           ,0
           ,NULL
           ,@MANDOB_NAME
           ,@CLIENT_REGION
           ,@CLIENT_PHONE_SMS
		   ,@B_COLOR
		   ,@CHECKED
		   ,@T_ID_BOX_CURCOR);

