                    a.DEBIT_IQ,
                    a.CREDIT_IQ,
                    SUM(a.DEBIT_IQ - a.CREDIT_IQ) OVER (ORDER BY a.BILL_DATE, a.B_CODE, a.BILL_NUMBER ASC), -- NULL, -- @BALANCEEE + @REMAIN_IQ (Last update)
                    a.DEBIT_CUR,
