                --     T_ITEMS (       COST)
                UPDATE T_ITEMS
                SET COST = @COST
                WHERE ITEM_CODE = @ITEM_CODE_CURSER;

                --     T_SALES_DETAILS     CURRANCY_EQUAL
                UPDATE T_SALES_DETAILS
                SET COST = @COST * UNIT_QTY * CURRANCY_EQUAL
                WHERE T_ID = @T_ID_ITEMS_CERSUR;

