@QTY	float	,
@UNIT_QTY	float	,
@SUBMIT_FLAG INT OUTPUT

AS




DECLARE


@STORE_CODE INT,

@UNIT_CODE INT,
@B_CODE INT,
@UNIT_NAME	nvarchar(50)	,


@COMPANY_TITAL	nvarchar(50)	,
@BILL_B  nvarchar(50),
@COST FLOAT,
@PRICE FLOAT
BEGIN TRAN
BEGIN
SELECT @BILL_B= ISNULL(MIN(BILL_NUMBER),0)-1 FROM T_BUY_DETAILS
SELECT @COMPANY_TITAL=COMPANY_TITAL  FROM T_COMPANY WHERE T_ID=1
SELECT TOP(1) @UNIT_NAME=UNIT_NAME,@PRICE=ISNULL(PRICE_SALE_1,0)  FROM T_ITEM_UNIT WHERE ITEM_CODE=@ITEM_CODE AND UNIT_QTY=@UNIT_QTY
SELECT @COST=ISNULL(AVG_BUY,0)*@UNIT_QTY  FROM V_ITEM_AVG_COST_BUY_MIN_UNIT WHERE ITEM_CODE=@ITEM_CODE
SET @STORE_CODE=0
IF @UNIT_QTY=1
BEGIN
SET @UNIT_CODE=1

END


ELSE    --  EEEEEELLLLLSSSSSEEEE

BEGIN
SET @UNIT_CODE=2


END


IF @QTY=0 GOTO ASDFSF
 SET @B_CODE=25;
 SET @QTY=@QTY*-1
INSERT INTO [dbo].[T_BUY_DETAILS]
           ([BILL_BRANCH]
           ,[BILL_NUMBER]
           ,[ITEM_CODE]
           ,[QTY]
           ,[UNIT_NAME]
           ,[UNIT_QTY]
           ,[STORE_CODE]
           ,[EXP_DATE]
           ,[PRICE_BUY]
           ,[CURRANCY_EQUAL]
           ,[PRICE]
           ,[TOTAL]
           ,[EXPENSES]
           ,[TOTAL_EXPENSES]
           ,[COST]
           ,[TOTAL_COST]
           ,[SELL1]
           ,[SELL2]
           ,[C_DATE]
           ,[ACTIVE])
     VALUES
           (@COMPANY_TITAL+@BILL_B
           ,@BILL_B
           ,@ITEM_CODE
           ,@QTY
           ,@UNIT_NAME
           ,@UNIT_QTY
           ,@STORE_CODE
           ,NULL
           ,@COST
           ,1
           ,@COST
           ,@COST*(@QTY)
           ,0
           ,0
           ,@COST
           ,@COST*(@QTY)
           ,@PRICE/@UNIT_QTY
           ,@PRICE
           ,GETDATE()
           ,0)

INSERT INTO [dbo].[T_STORE_BOX]
           ([BILL_BRANCH]
           ,[BILL_NUMBER]
           ,[BILL_DATE]
           ,[B_CODE]
           ,[WAJBA_CODE]
           ,[STORE_CODE]
           ,[EXP_DATE]
           ,[ITEM_CODE]
           ,[UNIT_CODE]
           ,[ST_IN]
           ,[ST_OUT]
           ,[QTY_UNIT]
           ,[C_DATE]
           ,[IS_AVG_COST]
           ,[AVG_COST]
           ,[ACTIVE])
     VALUES
           (@COMPANY_TITAL+@BILL_B
           ,@BILL_B
           ,GETDATE()
           ,@B_CODE
           ,0
           ,@STORE_CODE
           ,NULL
           ,@ITEM_CODE
           ,@UNIT_CODE
           ,@QTY
           ,0
           ,@UNIT_QTY
           ,GETDATE()
           ,0
           ,@COST
           ,1)

ASDFSF:



SET @SUBMIT_FLAG=1

END	--END OF PROGRAM

commit

































' 
END
GO
