        FROM
        (
            SELECT CAST(''<M>'' + REPLACE(@tmpJson, ''|'', ''</M><M>'') + ''</M>'' AS XML) AS Data
        ) AS A
        CROSS APPLY Data.nodes(''/M'') AS Split(a)
    ),
    KeyValuePairs AS
    (
        SELECT JsonObject,
               Value = LTRIM(RTRIM(SplitVal.a.value(''.'', ''NVARCHAR(MAX)'')))
        FROM SplitObjects
        CROSS APPLY 
        (
