                              ) + ''</N>'' AS XML)
        ) AS X(Val)
        CROSS APPLY Val.nodes(''/N'') AS SplitVal(a)
    ),
    Parsed AS
    (
        SELECT 
            JsonObject,
            [Key] = REPLACE(SUBSTRING(Value,1,CHARINDEX('':'',Value)-1),''"'',''''),
            [Value] = REPLACE(SUBSTRING(Value,CHARINDEX('':'',Value)+1,4000),''"'','''')
        FROM KeyValuePairs
    ),
    Pivoted AS
    (
        SELECT JsonObject,
               [ITEM_CODE],
