                       WHERE     (ITEM_CODE = dbo.T_BUY_DETAILS.ITEM_CODE) AND (BILL_BUY = dbo.T_BUY.BILL_NUMBER) AND (ACTIVE = 1)) AS REMAIN_BUY_MIN, dbo.T_BUY.CLIENT_CODE, dbo.T_BUY.CLIENT_NAME, dbo.T_BUY.BILL_DATE, dbo.T_BUY_DETAILS.IS_USED, 
