        SELECT
            ITEM_CODE,
            ISNULL(SUM(TOTAL_COST) / NULLIF(SUM(IIF(QTY = 0, NULL, QTY) * UNIT_QTY), 0), 0) AS AVG_BUY
        FROM dbo.T_BUY_DETAILS
        WHERE
            ((BILL_NUMBER = 0 AND ACTIVE = 0) OR (BILL_NUMBER <> 0 AND ACTIVE = 1))
        GROUP BY ITEM_CODE
    ) AS AVG_BUY ON I.ITEM_CODE = AVG_BUY.ITEM_CODE
' 
