                         C_DATE, ACTIVE, GUIDS,
						CASE
						 WHEN B_CODE=5 THEN
                             (SELECT        ISNULL(N''  /'' + FROM_ACC + N'' -   /'' + TO_ACC, N'' '') AS Expr1
                                FROM            dbo.T_SANAD
                                WHERE        (BILL_BRANCH LIKE dbo.V_BOX.BILL_BRANCH) AND (dbo.V_BOX.B_CODE = 5) AND ( ACCOUNT_CODE=dbo.V_BOX.ACCOUNT_CODE)) 
