                                FROM            dbo.T_SANAD_CNY
                                WHERE        (BILL_BRANCH LIKE dbo.V_BOX.BILL_BRANCH) AND (dbo.V_BOX.B_CODE = 15) AND ( ACCOUNT_CODE=dbo.V_BOX.ACCOUNT_CODE) ) 
								 WHEN B_CODE=28 THEN
