                                WHERE        (BILL_BRANCH LIKE dbo.V_BOX.BILL_BRANCH) AND (dbo.V_BOX.B_CODE = 28)AND ( ACCOUNT_CODE=dbo.V_BOX.ACCOUNT_CODE)) 
