    IU.ON_OFF = 1
    AND I.ACTIVE = 1
    AND (ISNULL(SB.STORE_CODE, -1) <> -1)
    AND (ST.IS_USED = 1)
    AND (ISNULL(SB.ACTIVE, 1) = 1)
